gameActivityClassAdditions.insert:

public void HuaweiPush_Get_Action_Intent_Data() {
     HuaweiPushPlugin.getActionIntentData();        
}

public void HuaweiPush_Get_Token() {
    HuaweiPushPlugin.getToken();
}                

public void HuaweiPush_Delete_Token() {
    HuaweiPushPlugin.deleteToken();
}

public void HuaweiPush_Subscribe(String topic) {
     HuaweiPushPlugin.subscribe(topic);
}

public void HuaweiPush_Unsubscribe(String topic) {
     HuaweiPushPlugin.unsubscribe(topic);
}

private void HuaweiPush_Set_Auto_Init_Enabled(boolean flag) {
    HuaweiPushPlugin.setAutoInitEnabled(flag);
}

//Native C++ functions
public native void nativeOnGetTokenSuccess(String token);
public native void nativeOnDeleteTokenSuccess();
public native void nativeOnNewToken(String token);
public native void nativeOnMessageReceived(String messageJson);
public native void nativeOnGetActionIntentDataSuccess(String dataJson);
public native void nativeOnDeleteTokenSuccess();
public native void nativeOnSubscribeSuccess();
public native void nativeOnUnSubscribeSuccess();
public native void nativeOnException(int errorcode, int action, String message);

gameActivityOnCreateAdditions.insert:

HuaweiPushPlugin.initialize(this, new HuaweiPushListener() {
    void onGetTokenSuccess(String token) {
        nativeOnGetTokenSuccess(token);        
    }

    void onDeleteTokenSuccess() {
        nativeDeleteTokenSuccess();        
    }

    void onException(int errorCode, int action, String message) {
        nativeOnException(errorCode, action, message);
    }

    void onNewToken(String token) {
       nativeOnNewToken(token);
    }

    void onMessageReceived(String messageJson) {
        nativeOnMessageReceived(messageJson);   
    }

    void onGetActionIntentDataSuccess(String dataJson) {
       nativeOnGetActionIntentDataSuccess(dataJson);
    }

    void onSubscribeSuccess() {
        nativeOnSubscribeSuccess();
    }

    void onUnSubscribeSuccess() {
       nativeOnUnSubscribeSuccess();   
    }
});
